local _PZ=game:GetService("Players")local _RS=game:GetService("RunService")local _UIS=game:GetService("UserInputService")local _LP=_PZ.LocalPlayer local _CAM=workspace.CurrentCamera local _SG=Instance.new("ScreenGui",game.CoreGui)_SG.Name="KamCamLockUI"_SG.ResetOnSpawn=false local msg=Instance.new("TextLabel")msg.Size=UDim2.new(0,250,0,30)msg.Position=UDim2.new(0.5,-125,0.03,0)msg.BackgroundTransparency=1 msg.TextColor3=Color3.fromRGB(255,255,255)msg.TextStrokeTransparency=0.4 msg.TextScaled=true msg.Font=Enum.Font.SourceSansBold msg.Text="Thanks for using Kamcams script"msg.Parent=_SG task.delay(5,function()for i=1,20 do msg.TextTransparency=i/20 msg.TextStrokeTransparency=0.4+(i/20)task.wait(0.05)end msg.Visible=false end) local _LB=Instance.new("TextButton")_LB.Size=UDim2.new(0,110,0,50)_LB.Position=UDim2.new(0.5,-55,0.12,0)_LB.AnchorPoint=Vector2.new(0.5,0)_LB.BackgroundColor3=Color3.fromRGB(30,30,30)_LB.TextColor3=Color3.fromRGB(255,255,255)_LB.Text="LOCK"_LB.Draggable=true _LB.Active=true _LB.Parent=_SG local _CL=false local _CT=nil local _LS=0.12 local _PA=0.125 local function _GLP(c)return c:FindFirstChild("UpperTorso")or c:FindFirstChild("Torso")or c:FindFirstChild("HumanoidRootPart")end local function _HT(t)local h=Instance.new("Highlight")h.FillColor=Color3.fromRGB(255,0,0)h.FillTransparency=0.3 h.OutlineTransparency=1 h.Name="KamTargetHighlight"h.Parent=t.Character end local function _RH()if _CT and _CT.Character then local o=_CT.Character:FindFirstChild("KamTargetHighlight")if o then o:Destroy()end end end local function _GPC()local c=nil local d=math.huge local m=Vector2.new(_CAM.ViewportSize.X/2,_CAM.ViewportSize.Y/2)for _,p in pairs(_PZ:GetPlayers())do if p~=_LP and p.Character then local part=_GLP(p.Character)if part then local s,onScreen=_CAM:WorldToViewportPoint(part.Position)if onScreen then local dist=(Vector2.new(s.X,s.Y)-m).Magnitude if dist<d then d=dist c=p end end end end end return c end local function _UL()_RH()_CL=false _CT=nil _LB.Text="LOCK"_LB.BackgroundColor3=Color3.fromRGB(30,30,30)end _RS.RenderStepped:Connect(function()if _CL and _CT and _CT.Character then local p=_GLP(_CT.Character)local h=_CT.Character:FindFirstChildOfClass("Humanoid")local down=false if h then local st=h:GetState()if st==Enum.HumanoidStateType.Physics or st==Enum.HumanoidStateType.GettingUp then down=true end if h.WalkSpeed<4 then down=true end end if p and h and h.Health>0 and not down then local pred=p.Position+p.Velocity*_PA local cf=CFrame.new(_CAM.CFrame.Position,pred)_CAM.CFrame=_CAM.CFrame:Lerp(cf,_LS)else _UL()end else if _CL then _UL()end end end) local function _TL()if _CL then _UL()else _CT=_GPC()if _CT then _RH()_HT(_CT)_CL=true _LB.Text="UNLOCK"_LB.BackgroundColor3=Color3.fromRGB(0,170,0)end end end _LB.MouseButton1Click:Connect(_TL)_UIS.InputBegan:Connect(function(i,g)if g then return end if i.UserInputType==Enum.UserInputType.Keyboard then if i.KeyCode==Enum.KeyCode.Q then _TL()end end end)_PZ.PlayerRemoving:Connect(function(p)if _CT==p then _UL()end end)
